inst_delta('(', 1).
inst_delta(')', -1).

inst_floors([], [], _).
inst_floors([I|IS], [F|FS], PrevFloor) :-
    inst_delta(I, Delta),
    F is PrevFloor + Delta,
    inst_floors(IS, FS, F).

puzzle_floors(Floors) :-
    puzzle_input(PuzStr),
    string_chars(PuzStr, PuzList),
    inst_floors(PuzList, Floors, 0).

part1_answer(N) :-
    puzzle_floors(Floors),
    last(Floors, N).

% Note that this returns all the times Santa is in floor -1, not just the
% first one
part2_answer(N) :-
    puzzle_floors(Floors),
    nth1(N, Floors, -1).